package JavaPractice;

public class Operations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float x=(float)7/2;
		System.out.println(x);
		
		int z= 2+(3-4)*5%8/4;
		System.out.println(z);

		int i=1;
		int j=4;
		int k=1;
		boolean r1=i>j;
		boolean r2=i>=j;
		boolean r3=j<k;
		boolean r4=i<=k;
		System.out.println(r1+" " + r2+ r3+ r4);
	}

}
